import React, { useEffect, useState } from 'react';
import '../../styles/confetti.css';

const Confetti = ({ show, duration = 5000, count = 200 }) => {
  const [pieces, setPieces] = useState([]);

  useEffect(() => {
    if (show) {
      // Create confetti pieces when shown
      const newPieces = [];

      for (let i = 0; i < count; i++) {
        const isMetallic = Math.random() < 0.3; // 30% chance for metallic effect

        newPieces.push({
          id: i,
          x: Math.random() * 100, // Random x position (0-100%)
          y: -10 - Math.random() * 10, // Start above viewport
          size: 5 + Math.random() * 10, // Random size (5-15px)
          color: getRandomColor(),
          rotation: Math.random() * 360, // Random initial rotation
          rotationSpeed: -5 + Math.random() * 10, // Random rotation speed
          speedX: -2 + Math.random() * 4, // Random horizontal speed
          speedY: 3 + Math.random() * 5, // Random fall speed
          shape: getRandomShape(),
          isMetallic: isMetallic,
          shine: isMetallic ? Math.random() * 100 : null // Position of shine effect
        });
      }

      setPieces(newPieces);

      // Clean up confetti after duration
      const timer = setTimeout(() => {
        setPieces([]);
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [show, count, duration]);
  // Get random color for confetti
  const getRandomColor = () => {
    const colors = [
      '#FFD700', // Gold
      '#FFC0CB', // Pink
      '#87CEFA', // Light Sky Blue
      '#00FFFF', // Cyan
      '#7FFFD4', // Aquamarine
      '#FF1493', // Deep Pink
      '#00FA9A', // Medium Spring Green
      '#FF7F50', // Coral
      '#E6E6FA', // Lavender
      '#FFFF00', // Yellow
      '#FF00FF', // Magenta
      '#F0E68C', // Khaki
      '#B0E0E6', // Powder Blue
      '#32CD32', // Lime Green
      '#FF4500', // Orange Red
      '#C0C0C0', // Silver
      '#FFFFFF', // White
      '#D8BFD8', // Thistle
      '#40E0D0', // Turquoise
      '#EE82EE'  // Violet
    ];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  // Get random shape for confetti
  const getRandomShape = () => {
    const shapes = ['rectangle', 'rectangle-h', 'rectangle-v', 'circle', 'square', 'strip'];
    return shapes[Math.floor(Math.random() * shapes.length)];
  };

  if (!show || pieces.length === 0) {
    return null;
  }

  return (
    <div className="confetti-container">
    {pieces.map((piece) => {
      const style = {
        left: `${piece.x}%`,
        top: `${piece.y}%`,
        backgroundColor: piece.color,
        transform: `rotate(${piece.rotation}deg)`,
        animation: `confetti-fall ${3 + Math.random() * 4}s linear forwards`,
        animationDelay: `${Math.random() * 2}s`,
        position: 'relative',
        overflow: 'hidden'
      };

      // Add additional props for metallic confetti
      const metallicStyle = piece.isMetallic ? {
        backgroundImage: `linear-gradient(${45 + piece.rotation}deg, transparent, rgba(255,255,255,0.8) ${piece.shine}%, transparent)`,
        boxShadow: '0 0 5px rgba(255,255,255,0.3)'
      } : {};

      switch (piece.shape) {
        case 'rectangle':
          return (
            <div 
              key={piece.id} 
              className={`confetti-piece ${piece.isMetallic ? 'metallic' : ''}`}
              style={{
                ...style,
                ...metallicStyle,
                width: `${piece.size * 3}px`,
                height: `${piece.size * 0.5}px`,
              }} 
            />
          );
          case 'rectangle-h':
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size * 4}px`,
                  height: `${piece.size * 0.4}px`,
                }} 
              />
            );
          case 'rectangle-v':
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size * 0.4}px`,
                  height: `${piece.size * 2.5}px`,
                }} 
              />
            );
          case 'square':
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size}px`,
                  height: `${piece.size}px`,
                }} 
              />
            );
          case 'circle':
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size}px`,
                  height: `${piece.size}px`,
                  borderRadius: '50%',
                }} 
              />
            );
          case 'strip':
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size * 4}px`,
                  height: `${piece.size * 0.3}px`,
                  borderRadius: `${piece.size * 0.15}px`,
                }} 
              />
            );
          default:
            return (
              <div 
                key={piece.id} 
                className="confetti-piece" 
                style={{
                  ...style,
                  width: `${piece.size * 2}px`,
                  height: `${piece.size * 0.5}px`,
                }} 
              />
            );
        }
      })}
    </div>
  );
};

export default Confetti;
