import React, { useState, useEffect } from 'react';

export default function GameSetup({ onSetupComplete }) {
  const [numPlayers, setNumPlayers] = useState(4); // Default: 1 human + 3 bots
  const [selectedCardBack, setSelectedCardBack] = useState('cardback'); // Default card back
  const [cardBacks, setCardBacks] = useState([
    { id: 'cardback', name: 'Classic Red' },
    { id: 'cardback_blue', name: 'Blue' },
    { id: 'cardback_green', name: 'Green' },
    { id: 'cardback_black', name: 'Black' },
    { id: 'cardback_orange', name: 'Orange' },
    { id: 'cardback_purple', name: 'Purple' }
  ]);

  // Start the game with selected options
  const handleStartGame = () => {
    onSetupComplete({
      numPlayers,
      cardBackStyle: selectedCardBack
    });
  };

  return (
    <div className="game-setup">
      <h2>Game of 17</h2>

      <div className="setup-section">
        <h3>Number of Players</h3>
        <p className="setup-instruction">Select the total number of players (including yourself):</p>

        <div className="player-selector">
          <button 
            className="selector-button"
            disabled={numPlayers <= 2}
            onClick={() => setNumPlayers(num => Math.max(2, num - 1))}
          >
            -
          </button>
          <span>{numPlayers} Players</span>
          <button 
            className="selector-button"
            disabled={numPlayers >= 7}
            onClick={() => setNumPlayers(num => Math.min(7, num + 1))}
          >
            +
          </button>
        </div>
      </div>

      <div className="setup-section">
        <h3>Card Back Style</h3>
        <p className="setup-instruction">Choose the style for the back of the cards:</p>

        <div className="card-back-selector">
          {cardBacks.map(cardBack => (
            <div 
              key={cardBack.id}
              className={`card-back-option ${selectedCardBack === cardBack.id ? 'selected' : ''}`}
              onClick={() => setSelectedCardBack(cardBack.id)}
            >
              <img 
                src={`/assets/cards/backs/${cardBack.id}.png`}
                alt={cardBack.name}
                title={cardBack.name}
              />
            </div>
          ))}
        </div>
      </div>

      <button className="start-button" onClick={handleStartGame}>
        Start Game
      </button>
    </div>
  );
}
