import React from 'react';
import PlayerHand from './PlayerHand';
import GameLog from './GameLog';

export default function GameBoard({ gameState, onDrawCard, onHold, onReset, onReturnHome }) {
    const { 
        players, 
        currentPlayerIndex, 
        phase, 
        winner: winningPlayerId, 
        gameLog 
    } = gameState;

    // Find the human player
    const humanPlayerIndex = players.findIndex(player => player.isHuman);
    const humanPlayer = players[humanPlayerIndex];

    // Position players evenly around a circle
    const positionPlayersInCircle = () => {
        if (players.length <= 1) return players;

        const arrangedPlayers = [...players];
        const totalPlayers = players.length;
        const angleStep = 360 / totalPlayers;

        // Calculate position for each player based on their index
        for (let i = 0; i < totalPlayers; i++) {
            // Calculate player's angle from bottom center (human player is always at 0 degrees)
            // Non-human players are placed at equal intervals going clockwise from human
            let playerAngle;

            if (players[i].isHuman) {
                playerAngle = 0; // Human player is always at bottom (0 degrees)
            } else {
                // Calculate bot placement: 
                // First, determine this bot's position relative to human player
                const orderAfterHuman = (i > humanPlayerIndex) ? 
                    (i - humanPlayerIndex) : 
                    (totalPlayers - humanPlayerIndex + i);

                // Calculate angle based on position order (clockwise)
                playerAngle = (orderAfterHuman * angleStep) % 360;
            }

            // Convert angle to position coordinates around the table
            // Table center is (50%, 50%) with radius depending on table size
            // We use cos for x-axis (horizontal) and sin for y-axis (vertical)
            // Note: In CSS, y-axis is inverted (top is 0), so we use -sin

            // For a circular table:
            // - Radius is 40% of container width/height for proper spacing
            // - Positions are calculated as percentage from center (50%, 50%)
            const radius = 40; // Percentage of container
            const xPos = 50 - radius * Math.sin((playerAngle * Math.PI) / 180);
            const yPos = 50 - radius * Math.cos((playerAngle * Math.PI) / 180);

            // Store calculated position and angle
            arrangedPlayers[i] = {
                ...players[i],
                position: {
                    left: `${xPos}%`,
                    top: `${yPos}%`,
                    angle: playerAngle
                }
            };
        }

        return arrangedPlayers;
    };

    const positionedPlayers = positionPlayersInCircle();

    return (
        <div className="game-board">
            <div className="game-table">
                <div className="players-container">
                    {positionedPlayers.map(player => {
                        // Calculate rotation for player hand
                        // Cards should be rotated to face the center of the table
                        let cardRotation = 0;

                        // If it's not the human player, calculate the rotation to face center
                        if (!player.isHuman) {
                            // Position angle + 180° makes cards face inward
                            cardRotation = player.position.angle + 180;
                        }

                        // Create custom style based on calculated position
                        const playerStyle = {
                            position: 'absolute',
                            left: player.position.left,
                            top: player.position.top,
                            transform: `translate(-50%, -50%) rotate(${cardRotation}deg)`,
                            width: player.isHuman ? '350px' : '280px',
                            zIndex: player.isHuman ? 5 : 2
                        };

                        return (
                            <div 
                                key={`player-${player.id}-${gameState.gameId || Date.now()}`}
                                className={`player-position ${player.isHuman ? 'human-player' : 'bot-player'}`}
                                style={playerStyle}
                            >
                                <PlayerHand 
                                    player={player}
                                    isCurrentTurn={player.id === currentPlayerIndex && phase === 'playing'}
                                    isHumanPlayer={player.isHuman}
                                    gamePhase={phase}
                                    showTotal={player.isHuman || phase === 'gameOver' || phase === 'revealing' || player.status === 'eliminated' || player.status === 'winner'}
                                    isWinner={player.id === winningPlayerId}
                                    gameId={gameState.gameId}
                                    cardBackStyle={cardBackStyle || 'cardback'}
                                />
                            </div>
                        );
                    })}
                </div>

                {phase === 'playing' && humanPlayer.id === currentPlayerIndex && (
                    <div className="player-controls">
                        <button 
                            className="action-button" 
                            onClick={onDrawCard}
                            disabled={humanPlayer.status !== 'active'}
                        >
                            Draw Card
                        </button>
                        <button 
                            className="action-button" 
                            onClick={onHold}
                            disabled={humanPlayer.status !== 'active' || humanPlayer.total < 11 || humanPlayer.cards.length < 2}
                        >
                            Hold
                        </button>
                    </div>
                )}

                <div className="game-info">
                    <GameLog messages={gameLog || []} />

                    {phase === 'gameOver' && (
                        <div className="game-over-controls">
                            <button className="action-button" onClick={onReset}>Play Again</button>
                            <button className="action-button" onClick={onReturnHome}>Main Menu</button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}
