import React, { useState, useEffect, useRef, Suspense } from 'react';
import { Canvas, useThree, useLoader } from '@react-three/fiber';
import * as THREE from 'three';
import { useSpring, animated } from '@react-spring/three';
import CardScene from '../../components/cards/CardScene';

// Camera controller component remains the same
function CameraController() {
    const { camera } = useThree();

    useEffect(() => {
        camera.position.set(0, 8, 2);
        camera.lookAt(0, 0, 0);
        camera.fov = 45;
        camera.updateProjectionMatrix();
    }, [camera]);

    return null;
}

// Animated card mesh component for the dealing animation
function AnimatedCardMesh({ suit, value, flipped, position, rotation, scale, cardBackStyle, delay }) {
    const frontTexture = useLoader(THREE.TextureLoader, `/cards/${suit}_${value}.png`);
    const backTexture = useLoader(THREE.TextureLoader, `/cards/${cardBackStyle}.png`);

    // Animation spring for card dealing
    const { pos, rot } = useSpring({
        from: { pos: [0, 15, 0], rot: [0, 0, Math.PI] },
        to: { pos: position, rot: rotation },
        delay: delay,
        config: { mass: 1, tension: 180, friction: 12 }
    });

    useEffect(() => {
        frontTexture.minFilter = THREE.LinearFilter;
        frontTexture.magFilter = THREE.LinearFilter;
        backTexture.minFilter = THREE.LinearFilter;
        backTexture.magFilter = THREE.LinearFilter;
    }, [frontTexture, backTexture]);

    return (
        <animated.mesh position={pos} rotation={rot} scale={scale}>
            <planeGeometry args={[1, 1.4]} />
            <meshBasicMaterial 
                map={flipped ? backTexture : frontTexture}
                side={THREE.DoubleSide}
                transparent={false}
            />
        </animated.mesh>
    );
}

export default function PlayerHand({
    player,
    isCurrentTurn,
    isHumanPlayer,
    gamePhase,
    showTotal,
    isWinner,
    gameId,
    cardBackStyle = 'cardback'
}) {
    const { name, cards, visibleCards, hiddenCards, total, status } = player;
    const canvasRef = useRef();
    const [animatingDeal, setAnimatingDeal] = useState(true);

    // This effect runs once when the component mounts to set up card dealing animation
    useEffect(() => {
        const dealTimer = setTimeout(() => {
            setAnimatingDeal(false);
        }, 1000); // Animation completes after 1 second

        return () => clearTimeout(dealTimer);
    }, []);

    // Clean up canvas WebGL context
    useEffect(() => {
        return () => {
            if (canvasRef.current) {
                const gl = canvasRef.current.__r3f?.gl;
                if (gl) {
                    gl.dispose();
                    gl.forceContextLoss();
                    const canvas = gl.domElement;
                    if (canvas && canvas.parentElement) {
                        canvas.parentElement.removeChild(canvas);
                    }
                }
            }
        };
    }, [gameId]);

    // Prepare cards data for the scene with appropriate position and rotation
    const prepareCardsForScene = () => {
        const allCards = [];

        // Determine whether to show hidden cards
        const showAllCards = gamePhase === 'gameOver' || 
                            status === 'eliminated' || 
                            status === 'winner' || 
                            gamePhase === 'revealing';

        // Calculate spacing based on card count
        const totalCards = hiddenCards.length + visibleCards.length;

        // Use consistent spacing for all players
        let cardSpacing = totalCards > 6 ? 0.12 
                        : totalCards === 5 ? 0.15 
                        : totalCards === 4 ? 0.18
                        : totalCards === 3 ? 0.25 
                        : 0.5;

        // Use consistent scale for all players
        const scale = [0.6, 0.6, 1];

        // Add hidden cards first with animation data
        hiddenCards.forEach((card, index) => {
            // Position cards in a horizontal row
            const xPos = (index - (totalCards - 1) / 2) * cardSpacing;

            allCards.push({
                id: `hidden-${player.id}-${index}-${gameId}`,
                suit: card.suit,
                value: card.value,
                flipped: !showAllCards,
                position: [xPos, 0, 0],
                rotation: [0, 0, 0],
                scale,
                animated: true,
                delay: index * 300 // Stagger the dealing animation
            });
        });

        // Add visible cards after hidden cards with animation data
        visibleCards.forEach((card, index) => {
            const startIndex = hiddenCards.length;
            const xPos = (startIndex + index - (totalCards - 1) / 2) * cardSpacing;

            allCards.push({
                id: `visible-${player.id}-${index}-${gameId}`,
                suit: card.suit,
                value: card.value,
                flipped: false,
                position: [xPos, 0, 0],
                rotation: [0, 0, 0],
                scale,
                animated: true,
                delay: (hiddenCards.length + index) * 300 // Continue staggered animation
            });
        });

        return allCards;
    };

    const getContainerClass = () => {
        const baseClass = `player-hand-container`;
        const statusClass = status;
        const activeClass = isCurrentTurn ? 'active-turn' : '';
        const winnerClass = isWinner ? 'winning-player' : '';
        const playerTypeClass = isHumanPlayer ? 'human-player' : 'bot-player';
        const animatingClass = animatingDeal ? 'dealing-animation' : '';

        return `${baseClass} ${statusClass} ${activeClass} ${winnerClass} ${playerTypeClass} ${animatingClass}`;
    };

    const getStatusText = () => {
        switch (status) {
            case 'active': return isCurrentTurn ? 'Playing...' : 'Waiting';
            case 'holding': return `Holding${showTotal ? ` at ${total}` : ''}`;
            case 'eliminated': return 'Busted!';
            case 'winner': return 'Winner!';
            default: return '';
        }
    };

    return (
        <div className={getContainerClass()}>
            <div className="player-info">
                <h3>{name} {isWinner && gamePhase === 'gameOver' && <span className="winner-badge">ðŸ‘‘</span>}</h3>
                <div className="player-status">
                    <span>{getStatusText()}</span>
                    <span>Total: {showTotal ? total : '?'}</span>
                </div>
            </div>

            <div className="player-cards-canvas">
                <Suspense fallback={<div className="loading-cards">Loading cards...</div>}>
                    <Canvas
                        ref={canvasRef}
                        shadows
                        key={`canvas-player-${player.id}-${gameId}`}
                    >
                        <CameraController />
                        <ambientLight intensity={0.8} />
                        <pointLight position={[10, 10, 10]} intensity={1.5} />
                        <CardScene 
                            cards={prepareCardsForScene()} 
                            cardBackStyle={cardBackStyle}
                            animatingDeal={animatingDeal}
                        />
                    </Canvas>
                </Suspense>
            </div>
        </div>
    );
}
