import React from 'react';

export default function PlayerHand({
  player,
  isCurrentTurn,
  isHumanPlayer,
  gamePhase,
  showTotal,
  isWinner,
}) {
  const { name, cards, visibleCards, hiddenCards, total, status } = player;

  const getStatusText = () => {
    switch (status) {
      case 'active':
        return isCurrentTurn ? 'Playing...' : 'Waiting';
      case 'holding':
        return `Holding${showTotal ? ` at ${total}` : ''}`;
      case 'eliminated':
        return 'Busted!';
      case 'winner':
        return 'Winner!';
      default:
        return '';
    }
  };

  const CardPlaceholder = ({ suit, value, flipped }) => (
    <div className={`card-placeholder ${flipped ? 'card-back' : 'card-front'}`}>
      {!flipped && (
        <div className="card-value">
          {value.charAt(0).toUpperCase()}
          {value.length > 1 ? value.charAt(1) : ''}
          <span className={`suit ${suit}`}>â™¥â™¦â™ â™£</span>
        </div>
      )}
    </div>
  );

  const showAllCards = gamePhase === 'gameOver' || gamePhase === 'revealing' || status === 'eliminated' || status === 'winner';

  return (
    <div className={`player-hand ${isCurrentTurn ? 'active-turn' : ''} ${status} ${isWinner ? 'winning-player' : ''}`}>
      <div className="player-info">
        <h3>
          {name} {isWinner && gamePhase === 'gameOver' && <span className="winner-badge">ðŸ‘‘</span>}
        </h3>
        <div className="player-status">
          <span>Total: {showTotal ? total : '?'}</span>
          <span>{getStatusText()}</span>
        </div>
      </div>

      <div className="cards-placeholder-container">
        {!showAllCards &&
          hiddenCards.map((card, index) => (
            <div key={`hidden-${player.id}-${index}`} className="card-wrapper">
              <CardPlaceholder suit={card.suit} value={card.value} flipped={true} />
            </div>
          ))}

        {showAllCards &&
          hiddenCards.map((card, index) => (
            <div key={`revealed-${player.id}-${index}`} className="card-wrapper">
              <CardPlaceholder suit={card.suit} value={card.value} flipped={false} />
            </div>
          ))}

        {visibleCards.map((card, index) => (
          <div key={`visible-${player.id}-${index}`} className="card-wrapper">
            <CardPlaceholder suit={card.suit} value={card.value} flipped={false} />
          </div>
        ))}
      </div>
    </div>
  );
}
