import React from 'react';
import Card from './Card';

export default function CardSelection({ cards }) {
  return (
    <group>
      {cards.map((card, index) => {
        // Position the 3 cards side by side
        const x = (index - 1) * 2;

        return (
          <Card
            key={`${card.suit}-${card.value}-selected`}
            suit={card.suit}
            value={card.value}
            position={[x, 0, 0]}
            rotation={[0, 0, 0]}
          />
        );
      })}
    </group>
  );
}
