import React from 'react';
import Card from './Card';
import { cardUtils } from '../utils/cardUtils';

export default function CardDeck({ onCardClick }) {
  // Generate all 52 cards
  const cards = cardUtils.generateDeck();

  return (
    <group>
      {cards.map((card, index) => {
        // Position all cards at the same coordinates to stack them
        const x = 0;
        const y = 0;
        const z = 0;

        return (
          <Card
            key={`${card.suit}-${card.value}`}
            suit={card.suit}
            value={card.value}
            position={[x, y, z]}
            scale={[2, 2, 2]} // Increase the scale to make the cards bigger
            onClick={() => onCardClick(card)}
            flipped={true}
          />
        );
      })}
    </group>
  );
}
