import React, { useEffect, useState } from 'react';
import '../styles/confetti.css';

const Confetti = ({ show, duration = 5000, count = 200 }) => {
  const [pieces, setPieces] = useState([]);

  useEffect(() => {
    if (show) {
      // Create confetti pieces when shown
      const newPieces = [];

      for (let i = 0; i < count; i++) {
        newPieces.push({
          id: i,
          x: Math.random() * 100, // Random x position (0-100%)
          y: -10 - Math.random() * 10, // Start above viewport
          size: 5 + Math.random() * 10, // Random size (5-15px)
          color: getRandomColor(),
          rotation: Math.random() * 360, // Random initial rotation
          rotationSpeed: -5 + Math.random() * 10, // Random rotation speed
          speedX: -2 + Math.random() * 4, // Random horizontal speed
          speedY: 3 + Math.random() * 5, // Random fall speed
          shape: getRandomShape()
        });
      }

      setPieces(newPieces);

      // Clean up confetti after duration
      const timer = setTimeout(() => {
        setPieces([]);
      }, duration);

      return () => clearTimeout(timer);
    }
  }, [show, count, duration]);

  // Get random color for confetti
  const getRandomColor = () => {
    const colors = [
      '#FF9F1C', // Orange
      '#FFBF69', // Light Orange
      '#2EC4B6', // Teal
      '#CBF3F0', // Light Teal
      '#FFFF82', // Light Yellow
      '#E71D36', // Red
      '#FF66B3', // Pink
      '#5D2E8C', // Purple
      '#70E000', // Green
      '#00B4D8', // Blue
    ];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  // Get random shape for confetti
  const getRandomShape = () => {
    const shapes = ['circle', 'square', 'triangle', 'star'];
    return shapes[Math.floor(Math.random() * shapes.length)];
  };

  if (!show || pieces.length === 0) {
    return null;
  }

  return (
    <div className="confetti-container">
      {pieces.map((piece) => {
        const style = {
          left: `${piece.x}%`,
          top: `${piece.y}%`,
          width: `${piece.size}px`,
          height: `${piece.size}px`,
          backgroundColor: piece.shape === 'circle' || piece.shape === 'square' ? piece.color : 'transparent',
          borderRadius: piece.shape === 'circle' ? '50%' : '0',
          transform: `rotate(${piece.rotation}deg)`,
          animation: `confetti-fall ${3 + Math.random() * 4}s linear forwards`,
          animationDelay: `${Math.random() * 2}s`,
        };

        if (piece.shape === 'triangle') {
          return (
            <div 
              key={piece.id} 
              className="confetti-piece confetti-triangle" 
              style={{
                ...style,
                borderBottom: `${piece.size}px solid ${piece.color}`,
                borderLeft: `${piece.size / 2}px solid transparent`,
                borderRight: `${piece.size / 2}px solid transparent`,
                backgroundColor: 'transparent',
                height: 0,
                width: 0
              }}
            />
          );
        } else if (piece.shape === 'star') {
          return (
            <div 
              key={piece.id} 
              className="confetti-piece confetti-star" 
              style={{
                ...style,
                backgroundColor: 'transparent',
                color: piece.color,
                fontSize: `${piece.size * 1.5}px`,
                lineHeight: `${piece.size}px`,
                textAlign: 'center'
              }}
            >
              â˜…
            </div>
          );
        } else {
          return (
            <div 
              key={piece.id} 
              className="confetti-piece" 
              style={style} 
            />
          );
        }
      })}
    </div>
  );
};

export default Confetti;
