import React, { useRef, useState } from 'react';
import { useLoader } from '@react-three/fiber';
import * as THREE from 'three';

export default function Card({ 
  suit, 
  value, 
  position = [0, 0, 0], 
  scale = [1, 1, 1], // Default scale
  onClick, 
  flipped = false 
}) {
  const meshRef = useRef();
  const [hovered, setHovered] = useState(false);

  // Load textures unconditionally to follow React Hook rules
  const frontTexturePath = `/assets/cards/faces/${suit}/${value}.png`;
  const backTexturePath = '/assets/cards/backs/cardback.png';

  // Load textures
  const frontTexture = useLoader(THREE.TextureLoader, frontTexturePath);
  const backTexture = useLoader(THREE.TextureLoader, backTexturePath);

  // Set texture filtering for better quality
  frontTexture.minFilter = THREE.LinearFilter;
  frontTexture.magFilter = THREE.LinearFilter;
  frontTexture.anisotropy = 16;

  backTexture.minFilter = THREE.LinearFilter;
  backTexture.magFilter = THREE.LinearFilter;
  backTexture.anisotropy = 16;

  // Card dimensions (standard playing card ratio)
  const width = 1;
  const height = 1.4;

  // Handle hover state
  const onPointerOver = () => setHovered(true);
  const onPointerOut = () => setHovered(false);

  return (
    <mesh
      ref={meshRef}
      position={position}
      scale={hovered ? [scale[0] * 1.1, scale[1] * 1.1, scale[2] * 1.1] : scale}
      onClick={onClick}
      onPointerOver={onPointerOver}
      onPointerOut={onPointerOut}
    >
      <planeGeometry args={[width, height]} />
      <meshStandardMaterial 
        map={flipped ? backTexture : frontTexture}
        side={THREE.DoubleSide}
      />
    </mesh>
  );
}
