import React, { useMemo } from 'react';
import * as THREE from 'three';
import { useLoader } from '@react-three/fiber';

export function CardMesh({ suit, value, flipped, position, rotation, scale }) {
  const frontTexturePath = `/assets/cards/faces/${suit}/${value}.png`;
  const backTexturePath = '/assets/cards/backs/cardback.png';

  const frontTexture = useLoader(THREE.TextureLoader, frontTexturePath);
  const backTexture = useLoader(THREE.TextureLoader, backTexturePath);

  const material = useMemo(() => {
    return new THREE.MeshBasicMaterial({
      map: flipped ? backTexture : frontTexture,
      side: THREE.DoubleSide,
    });
  }, [frontTexture, backTexture, flipped]);

  return (
    <mesh position={position} rotation={rotation} scale={scale}>
      <planeGeometry args={[1, 1.4]} />
      <meshBasicMaterial attach="material" {...material} />
    </mesh>
  );
}
