import React from 'react';
import { useFrame } from '@react-three/fiber';
import * as THREE from 'three';
import { useLoader } from '@react-three/fiber';

// Card mesh component for Three.js rendering
function CardMesh({ suit, value, flipped, position, rotation, scale }) {
    // Load textures
    const frontTexture = useLoader(THREE.TextureLoader, `/assets/cards/faces/${suit}/${value}.png`);
    const backTexture = useLoader(THREE.TextureLoader, '/assets/cards/backs/cardback.png');

    // Apply texture filters for better rendering
    React.useEffect(() => {
        [frontTexture, backTexture].forEach(texture => {
            texture.minFilter = THREE.LinearFilter;
            texture.magFilter = THREE.LinearFilter;
            texture.needsUpdate = true;
        });
    }, [frontTexture, backTexture]);

    return (
        <mesh position={position} rotation={rotation} scale={scale}>
            <planeGeometry args={[1, 1.4]} />
            <meshBasicMaterial 
                map={flipped ? backTexture : frontTexture}
                side={THREE.DoubleSide}
                transparent={true}
            />
        </mesh>
    );
}

export default function CardScene({ cards }) {
    return (
        <>
            {cards.map((card) => (
                <CardMesh
                    key={card.id}
                    suit={card.suit}
                    value={card.value}
                    flipped={card.flipped}
                    position={card.position}
                    rotation={card.rotation}
                    scale={card.scale}
                />
            ))}
        </>
    );
}
