import React, { Suspense, useRef, useEffect } from 'react';
import { Canvas, useThree } from '@react-three/fiber';
import * as THREE from 'three';
import CardScene from '../../components/cards/CardScene';

// Camera controller component to handle camera setup based on position
function CameraController({ position }) {
    const { camera } = useThree();

    useEffect(() => {
        // Adjust camera based on player position
        switch(position) {
            case 'left':
                // Side view for left player
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                break;
            case 'right':
                // Side view for right player
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                break;
            case 'top':
                // Looking down at cards for player across
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                break;
            case 'top-left':
                // Looking down at cards for player across left
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                break;
            case 'top-right':
                // Looking down at cards for player across right
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
                break;
            default:
                // Default for human player (bottom)
                camera.position.set(0, 0, 2);
                camera.lookAt(0, 0, 0);
        }

        camera.fov = 45;
        camera.updateProjectionMatrix();
    }, [camera, position]);

    return null;
}

export default function PlayerHand({
    player,
    isCurrentTurn,
    isHumanPlayer,
    gamePhase,
    showTotal,
    isWinner,
    gameId,
    position = 'bottom' // Default to bottom
}) {
    const { name, cards, visibleCards, hiddenCards, total, status } = player;
    const canvasRef = useRef();

    // Determine container class based on position
    const getContainerClass = () => {
        const baseClass = `player-hand-container ${position}`;
        const statusClass = status;
        const activeClass = isCurrentTurn ? 'active-turn' : '';
        const winnerClass = isWinner ? 'winning-player' : '';

        return `${baseClass} ${statusClass} ${activeClass} ${winnerClass}`;
    };

    // Helper function to get status text
    const getStatusText = () => {
        switch (status) {
            case 'active': return isCurrentTurn ? 'Playing...' : 'Waiting';
            case 'holding': return `Holding${showTotal ? ` at ${total}` : ''}`;
            case 'eliminated': return 'Busted!';
            case 'winner': return 'Winner!';
            default: return '';
        }
    };

    // Clean up canvas WebGL context
    useEffect(() => {
        return () => {
            if (canvasRef.current) {
                const gl = canvasRef.current.__r3f?.gl;
                if (gl) {
                    gl.dispose();
                    gl.forceContextLoss();
                    const canvas = gl.domElement;
                    if (canvas && canvas.parentElement) {
                        canvas.parentElement.removeChild(canvas);
                    }
                }
            }
        };
    }, [gameId]);

    // Prepare cards data for the scene with appropriate position and rotation
    const prepareCardsForScene = () => {
        const allCards = [];

        // Determine whether to show hidden cards
        const showAllCards = gamePhase === 'gameOver' || 
                            status === 'eliminated' || 
                            status === 'winner' || 
                            gamePhase === 'revealing';

        // Calculate spacing and scale based on position
        const totalCards = hiddenCards.length + visibleCards.length;
        const isHuman = position === 'bottom';

        // Adjust settings based on position
        const cardSpacing = isHuman ? 1.2 : 0.8; // Wider spacing for human player
        const baseScale = isHuman ? 0.7 : 0.8;
        const scale = [baseScale, baseScale, baseScale];

        // Get rotation based on position
        let baseRotation = [0, 0, 0];
        switch (position) {
            case 'top':
                baseRotation = [0, 0, Math.PI]; // Rotate 180 degrees
                break;
            case 'left':
                baseRotation = [0, 0, Math.PI/2]; // Rotate 90 degrees
                break;
            case 'right':
                baseRotation = [0, 0, -Math.PI/2]; // Rotate -90 degrees
                break;
            case 'top-left':
                baseRotation = [0, 0, Math.PI*3/4]; // Rotate 135 degrees
                break;
            case 'top-right':
                baseRotation = [0, 0, -Math.PI*3/4]; // Rotate -135 degrees
                break;
            default:
                baseRotation = [0, 0,0]; // No rotation for bottom player
        }

        // Add hidden cards
        hiddenCards.forEach((card, index) => {
            // Position cards in a slight arc
            const cardAngle = (index - (hiddenCards.length - 1) / 2) * 0.15;

            // Calculate position based on the card's position in hand
            let xPos, zPos;

            // Different positioning for different player positions
            if (position === 'bottom' || position === 'top') {
                xPos = cardSpacing * cardAngle * 8;
                zPos = 0;
            } else if (position === 'left' || position === 'right') {
                xPos = 0;
                zPos = cardSpacing * cardAngle * 8;
            } else {
                // Diagonal positions
                xPos = cardSpacing * cardAngle * 6;
                zPos = cardSpacing * cardAngle * 6;
            }

            // Final rotation is base rotation + individual card angle
            const finalRotation = [...baseRotation];
            if (position === 'bottom' || position === 'top') {
                finalRotation[2] += cardAngle * 0.2; // Slight fan effect
            } else if (position === 'left' || position === 'right') {
                finalRotation[2] += cardAngle * 0.2;
            } else {
                finalRotation[2] += cardAngle * 0.2;
            }

            allCards.push({
                id: `hidden-${player.id}-${index}-${gameId}`,
                suit: card.suit,
                value: card.value,
                flipped: !showAllCards,
                position: [xPos, 0, zPos],
                rotation: finalRotation,
                scale
            });
        });

        // Add visible cards
        visibleCards.forEach((card, index) => {
            const startIndex = hiddenCards.length;
            const cardAngle = (startIndex + index - (totalCards - 1) / 2) * 0.15;

            // Calculate position based on the card's position in hand
            let xPos, zPos;

            // Different positioning for different player positions
            if (position === 'bottom' || position === 'top') {
                xPos = cardSpacing * cardAngle * 4;
                zPos = 0;
            } else if (position === 'left' || position === 'right') {
                xPos = 0;
                zPos = cardSpacing * cardAngle * 8;
            } else {
                // Diagonal positions
                xPos = cardSpacing * cardAngle * 6;
                zPos = cardSpacing * cardAngle * 6;
            }

            // Final rotation is base rotation + individual card angle
            const finalRotation = [...baseRotation];
            if (position === 'bottom' || position === 'top') {
                finalRotation[2] += cardAngle * 0.2; // Slight fan effect
            } else if (position === 'left' || position === 'right') {
                finalRotation[2] += cardAngle * 0.2;
            } else {
                finalRotation[2] += cardAngle * 0.2;
            }

            allCards.push({
                id: `visible-${player.id}-${index}-${gameId}`,
                suit: card.suit,
                value: card.value,
                flipped: false,
                position: [xPos, 0, zPos],
                rotation: finalRotation,
                scale
            });
        });

        return allCards;
    };

    // Calculate height of card canvas based on position
    const getCanvasHeight = () => {
        switch (position) {
            case 'bottom':
                return '200px'; // Larger for human player
            case 'left':
            case 'right':
                return '180px';
            default:
                return '160px';
        }
    };

    return (
        <div className={getContainerClass()}>
            <div className="player-info">
                <h3>{name} {isWinner && gamePhase === 'gameOver' && <span className="winner-badge">ðŸ‘‘</span>}</h3>
                <div className="player-status">
                    <span>{getStatusText()}</span>
                    <span>Total: {showTotal ? total : '?'}</span>
                </div>
            </div>

            <div className="player-cards-canvas" style={{ height: getCanvasHeight() }}>
                <Suspense fallback={<div className="loading-cards">Loading cards...</div>}>
                    <Canvas
                        ref={canvasRef}
                        shadows
                        key={`canvas-player-${player.id}-${gameId}`}
                    >
                        <CameraController position={position} />
                        <ambientLight intensity={0.8} />
                        <pointLight position={[10, 10, 10]} intensity={1.5} />
                        <CardScene cards={prepareCardsForScene()} />
                    </Canvas>
                </Suspense>
            </div>
        </div>
    );
}
