import React, { Suspense, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import CardDeck from './CardDeck';
import CardSelection from './CardSelection';
import { cardUtils } from '../utils/cardUtils';

export default function Scene() {
  const [selectedCards, setSelectedCards] = useState([]);
  const [showRandomCards, setShowRandomCards] = useState(false);

  // Generate a full deck
  const fullDeck = cardUtils.generateDeck();

  const handleRandomDraw = () => {
    const drawnCards = cardUtils.drawCards(fullDeck, 3);
    setSelectedCards(drawnCards);
    setShowRandomCards(true);
  };

  const handleReturn = () => {
    setSelectedCards([]);
    setShowRandomCards(false);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        zIndex: 100,
      }}>
        <button
          onClick={handleRandomDraw}
          style={{
            padding: '10px',
            background: '#333',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            marginRight: '10px'
          }}
        >
          Draw 3 Random Cards
        </button>

        {showRandomCards && (
          <button
            onClick={handleReturn}
            style={{
              padding: '10px',
              background: '#555',
              color: 'white',
              border: 'none',
              borderRadius: '5px'
            }}
          >
            Return to Deck
          </button>
        )}
      </div>

      <Canvas camera={{ position: [0, 0, 10], fov: 75 }}>
        <color attach="background" args={['#2c6e49']} />
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} intensity={1} />
        <Suspense fallback={null}>
          {showRandomCards ? (
            <CardSelection cards={selectedCards} />
          ) : (
            <CardDeck onCardClick={(card) => console.log('Selected card:', card)} />
          )}
          <OrbitControls enablePan={true} enableZoom={true} enableRotate={false} />
        </Suspense>
      </Canvas>
    </div>
  );
}
