import React, { Suspense } from 'react';
import Card from './Card';

export default function PlayerHand({ player, isCurrentTurn, isHumanPlayer, gamePhase, showTotal }) {
  const { name, cards, visibleCards, hiddenCards, total, status } = player;

  // Helper function to get status text
  const getStatusText = () => {
    switch(status) {
      case 'active': return isCurrentTurn ? 'Playing...' : 'Waiting';
      case 'holding': return `Holding${showTotal ? ` at ${total}` : ''}`;
      case 'eliminated': return 'Busted!';
      case 'winner': return 'Winner!';
      default: return '';
    }
  };

  const CardFallback = () => (
    <div style={{ 
      width: '60px', 
      height: '84px', 
      backgroundColor: '#1b4332',
      border: '1px solid #43aa8b',
      borderRadius: '4px',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center'
    }}>
      <span>Loading</span>
    </div>
  );

  // Determine whether to show hidden cards (show them when game is over or player is eliminated/winner)
  const showAllCards = gamePhase === 'gameOver' || status === 'eliminated' || status === 'winner';

  return (
    <div className={`player-hand ${isCurrentTurn ? 'active-turn' : ''} ${status}`}>
      <div className="player-info">
        <h3>{name}</h3>
        <div className="player-status">
          <span>Total: {showTotal ? total : '?'}</span>
          <span>{getStatusText()}</span>
        </div>
      </div>

      <div className="cards-container">
        {/* Hidden cards (for bots) - show as hidden unless game is over or player is eliminated/winner */}
        {!showAllCards && hiddenCards.map((card, index) => (
          <div key={`hidden-${index}`} className="card-wrapper">
            <Suspense fallback={<CardFallback />}>
              <Card
                suit={card.suit}
                value={card.value}
                flipped={true} // Always show back while hidden
                position={[0, 0, 0]}
                scale={[1.2, 1.2, 1.2]}
              />
            </Suspense>
          </div>
        ))}

        {/* Show hidden cards as visible when game is over or player is eliminated/winner */}
        {showAllCards && hiddenCards.map((card, index) => (
          <div key={`revealed-${index}`} className="card-wrapper">
            <Suspense fallback={<CardFallback />}>
              <Card
                suit={card.suit}
                value={card.value}
                flipped={false} // Show face when game is over
                position={[0, 0, 0]}
                scale={[1.2, 1.2, 1.2]}
              />
            </Suspense>
          </div>
        ))}

        {/* Visible cards */}
        {visibleCards.map((card, index) => (
          <div key={`visible-${index}`} className="card-wrapper">
            <Suspense fallback={<CardFallback />}>
              <Card
                suit={card.suit}
                value={card.value}
                flipped={false} // Show face
                position={[0, 0, 0]}
                scale={[1.2, 1.2, 1.2]}
              />
            </Suspense>
          </div>
        ))}
      </div>
    </div>
  );
}
