            const player = {
                id: i,
                name: playerName,
                isHuman: i === 0,
                cards: [...drawn],
                hiddenCards: i === 0 ? [] : [...drawn], // For bots, initial card is hidden
                visibleCards: i === 0 ? [...drawn] : [], // For human, initial card is visible
                total: cardUtils.calculateTotal(drawn),
                status: 'active' // active, holding, eliminated, winner
