        initialLog.push("Your turn.");

        setGameState({
            phase: 'playing',
            players,
            deck, // Store the updated deck after dealing all initial cards
            currentPlayerIndex: 0,
            winner: null,
            gameLog: initialLog
        });
    };

    // Handle player's decision to draw a card
