        const player = updatedPlayers[gameState.currentPlayerIndex];

        player.cards = [...player.cards, drawnCard];
        player.visibleCards = [...player.visibleCards, drawnCard];
        player.total = cardUtils.calculateTotal(player.cards);

        // Log the action
        const actionLog = `${player.name} drew ${drawnCard.value} of ${drawnCard.suit}. Total: ${player.total}`;

        // Check if player reached 17
        if (player.total === 17) {
            player.status = 'winner';

            setGameState({
                ...gameState,
                players: updatedPlayers,
                deck: remaining,
                phase: 'gameOver',
                winner: gameState.currentPlayerIndex,
                gameLog: [...gameState.gameLog, actionLog, `${player.name} won with exactly 17!`]
            });
            return;
