            // Decide whether to draw or hold
            if (mustDrawFirstCard || currentPlayer.total < 11 ||
                cardUtils.shouldBotDraw(currentPlayer.cards, otherPlayersVisibleCards)) {
                handleDrawCard();
            } else {
                // Bot decides to hold
                const updatedPlayers = [...gameState.players];
                updatedPlayers[gameState.currentPlayerIndex].status = 'holding';

                // Find next active player
                let nextPlayerIndex = (gameState.currentPlayerIndex + 1) % gameState.players.length;
                while (
                    nextPlayerIndex !== gameState.currentPlayerIndex &&
                    (updatedPlayers[nextPlayerIndex].status === 'eliminated' ||
                        updatedPlayers[nextPlayerIndex].status === 'holding')
                ) {
                    nextPlayerIndex = (nextPlayerIndex + 1) % gameState.players.length;
                }

                // Check if all players are holding or eliminated
                const allDone = updatedPlayers.every(p => p.status !== 'active');

                if (allDone) {
                    // Check if game should end using the shared game end logic
                    const gameEndCheck = checkGameEnd(updatedPlayers,
                        `${currentPlayer.name} decided to hold with a total of ${currentPlayer.total}.`);

                    if (gameEndCheck.gameOver) {
                        setGameState({
                            ...gameState,
                            players: gameEndCheck.players,
                            deck: gameState.deck,
                            phase: 'revealing',
                            revealPhase: true,
                            pendingWinner: gameEndCheck.winner,
                            pendingWinnerMessage: gameEndCheck.winnerMessage,
                            gameLog: [...gameState.gameLog, ...gameEndCheck.message]
                        });
                        return;
                    }
                }

                setGameState({
                    ...gameState,
                    players: updatedPlayers,
                    currentPlayerIndex: nextPlayerIndex,
                    gameLog: [...gameState.gameLog,
                    `${currentPlayer.name} decided to hold with a total of ${currentPlayer.total}.`,
                    `${updatedPlayers[nextPlayerIndex].name}'s turn.`
                    ]
                });
            }
        }, 1000); // 1 second delay for bot actions

        return () => clearTimeout(botTurn);
    }, [gameState]);



    // Add this useEffect to handle the reveal phase
    useEffect(() => {
        // Only run this effect when we're in the revealing phase
        if (gameState.phase === 'revealing' && gameState.revealPhase) {
            const revealTimer = setTimeout(() => {
                const updatedPlayers = [...gameState.players];

                // After revealing, set the winner status
                if (gameState.pendingWinner !== -1) {
                    updatedPlayers[gameState.pendingWinner].status = 'winner';
                }

                setGameState({
                    ...gameState,
                    players: updatedPlayers,
                    phase: 'gameOver',
                    revealPhase: false,
                    winner: gameState.pendingWinner,
                    gameLog: [...gameState.gameLog, gameState.pendingWinnerMessage]
                });
            }, 500); // 2 second delay to allow players to see all cards

            return () => clearTimeout(revealTimer);
        }
    }, [gameState.phase, gameState.revealPhase]);

    const resetGame = () => {
        // Reset the game with the same number of players
        const numPlayers = gameState.players.length;
        setupGame(numPlayers);
    };

    const returnToHome = () => {
        // Reset everything and return to setup phase
        setGameState({
            phase: 'setup',
            players: [],
            deck: [],
            currentPlayerIndex: 0,
            winner: null,
            gameLog: []
        });
    };

    return (
        <div className="game-container">
            {gameState.phase === 'setup' ? (
                <GameSetup onSetupComplete={setupGame} />
            ) : (
                <GameBoard
                    gameState={gameState}
                    onDrawCard={handleDrawCard}
                    onHold={handleHold}
                    onReset={resetGame}
                    onReturnHome={returnToHome}
                />
            )}
        </div>
    );
}
